    private Adapter mAdapter;
    private Button mMapCodeBtn;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_map_code);

        mMapCodeBtn = findViewById(R.id.mapcodebtn);
        mMapCodeBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                finish();
            }
        });

        RecyclerView recyclerView = findViewById(R.id.recyclerview);
        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(this);
        recyclerView.setLayoutManager(linearLayoutManager);

        mAdapter = new Adapter();
        recyclerView.setAdapter(mAdapter);

        InputStream inputStream = getResources().openRawResource(R.raw.europe_mapcode);
        ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
        int t;
        try {
            t = inputStream.read();
            while (t != -1) {
                byteArrayOutputStream.write(t);
                t = inputStream.read();
            }
            EUROPE_MAPCODE = new String(byteArrayOutputStream.toByteArray(), "UTF-8");
            inputStream.close();
        } catch (IOException e) {
            e.printStackTrace();
        }

        List<Data> list = Arrays.asList(
                new Data("Korea Map", "made by Hosung.Kim", KOREA_MAPCODE),
                new Data("Seoul Map", "made by Hosung.Kim", SEOUL_MAPCODE),
                new Data("Three Kingdoms Map", "made by Hosung.Kim", THREEKINGDOMS_MAPCODE),
                new Data("United Kingdom Map", "made by Hosung.Kim", UNITEDKINGDOMS_MAPCODE),
                new Data("Europe Map", "made by Hosung.Kim", EUROPE_MAPCODE),
                new Data("World Map", "made by Hosung.Kim", WORLD_MAPCODE)
        );

        for (int i=0; i<list.size(); i++) {
            int finalI = i;
            list.get(i).setOnItemClickListener(new OnItemClickListener() {
                @Override
                public void onClick1() {
                    ClipboardManager clipboardManager = (ClipboardManager) getSystemService(Context.CLIPBOARD_SERVICE);
                    ClipData clip = ClipData.newPlainText("simple text", list.get(finalI).getMapCode());
                    clipboardManager.setPrimaryClip(clip);
                }

                @Override
                public void onClick2() {
                    if (list.get(finalI).getMapCode().contains("HexMapCode")) {
                        Intent intent = new Intent(MapCodeActivity.this, HexMapActivity.class);
                        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
                        intent.putExtra("mode", 1);
                        intent.putExtra("mapCode", list.get(finalI).getMapCode());
                        getBaseContext().startActivity(intent);
                        //mDialog1.dismiss();
                    } else {
                        Toast.makeText(getBaseContext(), "올바른 MapCode를 입력하세요.", Toast.LENGTH_LONG).show();
                    }
                }


            });
            mAdapter.addItem(list.get(i));
        }
        mAdapter.notifyDataSetChanged();
    }
